<template>    <div class="file">        <Item v-for="item in mainStore.fileList" :name="item.filename" :id="item.id"              :filetype="item.filetype" :datetime="item.createTime" :key="item.id"/>    </div></template><script>import {useMainStore} from '@/store'import {onMounted, watch} from 'vue'import {useRoute} from 'vue-router'import Item from '@/components/Item'export default {    name: 'File',    components: {Item},    setup(){        let mainStore = useMainStore()        let route = useRoute();        onMounted(() => {            if (route.params.id === undefined){                mainStore.file_id = 0            } else {                mainStore.file_id = parseInt(route.params.id)            }            mainStore.text = '文件'            mainStore.status = 1            mainStore.init()        })        watch(() => route.path, (newVal) => {            let strings = newVal.split('/')            if (!isNaN(parseInt(strings[strings.length - 1]))) {                mainStore.toDir(strings[strings.length - 1])            }        });        return {            mainStore        }    }}</script><style scoped>.file{    padding: 20px;    display: flex;    flex-wrap: wrap;    align-content: flex-start;    border-radius: 10px;}</style>